          <span>Live interactive artifact</span>
          {copied && <span className="text-green-600">Copied to clipboard!</span>}
        </div>
      </div>
    </div>
  );
}

client/src/components/chat-input.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Send, Shield, Paperclip, X } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ChatInputProps {
  onSend: (message: string, attachments?: File[]) => void;
  disabled?: boolean;
  placeholder?: string;
}

export function ChatInput({ onSend, disabled = false, placeholder = "Ask me anything..." }: ChatInputProps) {
  const [message, setMessage] = useState("");
  const [attachments, setAttachments] = useState<File[]>([]);
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleSubmit = () => {
    if ((message.trim() || attachments.length > 0) && !disabled) {
      onSend(message.trim(), attachments);
      setMessage("");
      setAttachments([]);
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    const validFiles: File[] = [];

    for (const file of files) {
      // Check file size (10MB limit)
      if (file.size > 10 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: `${file.name} is larger than 10MB. Please select a smaller file.`,
          variant: "destructive",
        });
        continue;
      }

      // Check file type
      const allowedTypes = [
        'text/plain',
        'text/markdown',
        'text/csv',
        'application/pdf',
        'application/msword',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
      ];

      if (!allowedTypes.includes(file.type)) {
        toast({
          title: "Unsupported file type",
          description: `${file.name} is not a supported file type. Please select a text, PDF, or document file.`,
          variant: "destructive",
        });
        continue;
      }

      validFiles.push(file);
    }

    setAttachments(prev => [...prev, ...validFiles]);

    // Reset the input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const removeAttachment = (index: number) => {
    setAttachments(prev => prev.filter((_, i) => i !== index));
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  // Auto-resize textarea
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "auto";
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [message]);

  return (
    <div className="w-full max-w-2xl">
      {/* Attachments Preview */}
      {attachments.length > 0 && (
        <div className="mb-3 flex flex-wrap gap-2">
          {attachments.map((file, index) => (
            <div key={index} className="flex items-center bg-gray-100 rounded-lg px-3 py-2 text-sm">
              <Paperclip className="w-4 h-4 mr-2 text-gray-500" />
              <span className="truncate max-w-32">{file.name}</span>
              <Button
                variant="ghost"
                size="sm"
                className="ml-2 h-4 w-4 p-0 hover:bg-gray-200"
                onClick={() => removeAttachment(index)}
              >
                <X className="w-3 h-3" />
              </Button>
            </div>
          ))}
        </div>
      )}

      <div className="relative">
        {/* Hidden File Input */}
        <input
          ref={fileInputRef}
          type="file"
          multiple
          accept=".txt,.md,.csv,.pdf,.doc,.docx"
          onChange={handleFileSelect}
          className="hidden"
        />

        <Textarea
          ref={textareaRef}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
          disabled={disabled}
          className="w-full p-4 pl-12 pr-20 border-2 border-gray-200 rounded-xl resize-none focus:border-primary focus:ring-2 focus:ring-primary/20 outline-none transition-all min-h-[60px] max-h-32"
          rows={1}
        />

        {/* Attachment Button - Inside chatbox, left side */}
        <Button
          variant="ghost"
          size="sm"
          className="absolute left-3 bottom-3 h-8 w-8 p-0 text-gray-500 hover:text-gray-700 hover:bg-gray-100"
          onClick={triggerFileInput}
          disabled={disabled}
        >
          <Paperclip className="w-4 h-4" />
        </Button>

        {/* Send Button - Inside chatbox, right side */}
        <Button
          onClick={handleSubmit}
          disabled={(!message.trim() && attachments.length === 0) || disabled}
          size="sm"
          className="absolute right-3 bottom-3 h-8 w-8 p-0 bg-primary hover:bg-primary/90"
        >
          <Send className="w-4 h-4" />
        </Button>
      </div>

      <div className="flex items-center justify-between mt-3 text-sm text-gray-500">
        <span>Press Enter to send, Shift+Enter for new line</span>
        <span className="flex items-center space-x-1">
          <Shield className="w-3 h-3" />
          <span>Secure & Private</span>
        </span>
      </div>
    </div>
  );
}

client/src/components/collection-card.ts.content:
import { Folder, MoreHorizontal } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import type { CollectionWithStats } from "@shared/schema";
import { useLocation } from "wouter";
import { useDeleteCollection } from "@/hooks/use-collections";

interface CollectionCardProps {
  collection: CollectionWithStats;
  onStartChat?: (collectionId: number) => void;
}

export function CollectionCard({ collection, onStartChat }: CollectionCardProps) {
  const [, setLocation] = useLocation();
  const deleteCollection = useDeleteCollection();

  const getCollectionColor = (id: number) => {
    const colors = [
      "bg-blue-100 text-blue-600",
      "bg-green-100 text-green-600", 
      "bg-purple-100 text-purple-600",
      "bg-orange-100 text-orange-600",
      "bg-pink-100 text-pink-600",
      "bg-cyan-100 text-cyan-600"
    ];
    return colors[id % colors.length];
  };

  const handleStartChat = () => {
    if (onStartChat) {
      onStartChat(collection.id);
    }
  };

  const handleViewCollection = () => {
    setLocation(`/collections/${collection.id}`);
  };

  const handleDelete = () => {
    if (confirm("Are you sure you want to delete this collection? This will also delete all associated documents and conversations.")) {
      deleteCollection.mutate(collection.id);
    }
  };

  return (
    <Card className="hover:shadow-lg transition-shadow cursor-pointer">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${getCollectionColor(collection.id)}`}>
            <Folder className="w-6 h-6" />
          </div>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                <MoreHorizontal className="w-4 h-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={handleViewCollection}>
                View Details
              </DropdownMenuItem>
              <DropdownMenuItem onClick={handleDelete} className="text-red-600">
                Delete Collection
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <h3 className="text-lg font-semibold text-gray-900 mb-2">{collection.name}</h3>
        <p className="text-gray-600 text-sm mb-4 line-clamp-2">{collection.description || "No description provided"}</p>

        <div className="flex items-center justify-between text-sm mb-4">
          <span className="text-gray-500">{collection.documentCount} documents</span>
          <span className="text-gray-500">
            {collection.lastUsed && collection.lastUsed !== "Invalid Date" 
              ? (() => {
                  const date = new Date(collection.lastUsed);
                  return !isNaN(date.getTime()) ? date.toLocaleDateString() : "Never used";
                })()
              : "Never used"
            }
          </span>
        </div>

        <div className="flex space-x-2 pt-4 border-t border-gray-100">
          <Button onClick={handleStartChat} className="flex-1 bg-primary hover:bg-primary/90 text-white">
            Open Chat
          </Button>
          <Button onClick={handleViewCollection} variant="outline" className="px-3">
            View
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

